mapper.update:
update member_info set
divide_type = #{divideType}, plan_id = #{planId}
where member_id in

    #{mid}

update member_info set
divide_type = #{divideType}, plan_id = #{planId}
where member_id = #{agentId}

mapper.select:
select m.id as agentId, m.account as agentName, m.enabled as accountStatus, m.agent_level as levelNum, m.parent_id as parentId, m.parent_name as parentName, m.super_path as agentPath, -- srs.sub_member as subMember, -- srs.sub_agent as subAgent, mi.divide_type as divideType, mi.plan_id as planId, rslp.plan_name as planName from member as m inner join member_info as mi on m.id = mi.member_id left join rebate_same_level_plan as rslp on mi.plan_id = rslp.plan_id
m.user_type = 'A' and m.account != 'webRoot' and m.account != 'wapRoot' and m.account != 'testRoot' and mi.divide_type = 1
and m.account = #{agentName}
