mapper.select:
SELECT dl.id as id, dl.user_id as userId, dl.user_name as userName, dl.divide_config as divideConfig, dl.create_time as createTime, dl.create_by as createBy, dl.update_time as updateTime, dl.update_by as updateBy, m.user_type as userType, m.parent_id as parentId, m.parent_name as parentName, m.agent_level as agentLevel, m.super_path as superPath FROM divide_layer_config dl INNER JOIN member m on m.id = dl.user_id where 1 = 1
and m.parent_name = #{dto.agentName} and dl.user_name != #{dto.agentName}
and dl.user_name = #{dto.userName}
and dl.user_name = #{dto.userName}
and m.agent_level = 1
and m.user_type = 'A' order by dl.create_time desc

        select IFNULL(max(maxRatio),0) as minRatio from (
            select JSON_UNQUOTE(
                    json_extract(
                            json_extract(
                                dl.divide_config,CONCAT('$.',concat('"', concat(#{code},'"')))),CONCAT('$.',concat('"', concat('divideRatio','"'))
                            )
                        )
                    ) AS maxRatio
            from divide_layer_config dl INNER JOIN member m on dl.user_id = m.id
	        where m.parent_name = #{userName} and m.account != #{userName}
	    ) tmp
