mapper.select:
select a.id, a.statistics_time, a.customer_code, a.game_kind, a.valid_amount, a.win_amount, a.accumulate_win_amount, a.game_type, a.start_time, a.end_time, a.create_time, a.create_by, b.name game_name, c.name platform_name, d.game_type_name, d.id game_type_id from game_financial_report a left join game_kind as b on a.game_kind = b.code left join game_platform as c on a.platform_code = c.code left join game_type as d on a.game_type = d.game_type_code
and a.statistics_time = #{statisticsTime}
and a.game_type = #{gameType}
and a.platform_code = #{platformCode}

select a.id, a.statistics_time, a.customer_code, a.game_kind, a.valid_amount, a.win_amount, a.accumulate_win_amount, a.game_type, a.start_time, a.end_time, a.create_time, a.create_by, b.name game_name, c.name platform_name, d.game_type_name, d.id game_type_id from game_financial_report a left join game_kind as b on a.game_kind = b.code left join game_platform as c on a.platform_code = c.code left join game_type as d on a.game_type = d.game_type_code
and a.statistics_time = #{dto.statisticsTime}
and a.game_type = #{dto.gameType}
and a.platform_code = #{dto.platformCode}
order by d.id

SELECT #{statisticsTime} statistics_time, #{customerCode} customer_code, g.code game_kind, g.platform_code, g.game_type, ROUND(IFNULL( c.valid_amount, 0 ), 2) valid_amount, ROUND(IFNULL( c.win_amount, 0 ), 2) win_amount, ROUND(IFNULL( c.accumulate_win_amount, 0 ), 2) accumulate_win_amount, #{startTime} start_time, #{endTime} end_time FROM game_kind g LEFT JOIN ( SELECT a.valid_amount, a.win_amount, a.platform_code, a.game_type, a.game_kind, b.accumulate_win_amount FROM ( SELECT sum(valid_amount) valid_amount, sum(win_amount) win_amount, platform_code, game_kind, game_type FROM game_bet_daily_report
and stat_time >= #{startTime}
and stat_time <= #{endTime}
GROUP BY game_kind ) a LEFT JOIN ( SELECT sum(win_amount) accumulate_win_amount, game_kind FROM game_bet_daily_report
and stat_time <= #{endTime}
GROUP BY game_kind ) b on a.game_kind = b.game_kind ) c on g.code = c.game_kind
