mapper.select:
SELECT gameName, lottType, case when lottType = 1 then '官方彩' when lottType = 2 then '自营彩' when lottType = 3 then '六合彩' end lottTypeName, gameCode, userCount, betCount, betAmount, validAmount, winAmount, betAmount - Winamount sendPrizeAmount, ROUND(betAmount / usercount, 2) averageBetAmount, #{account} account, #{proxyAccount} proxyAccount, case when #{isDirectly} = 0 then '否' when #{isDirectly} = 1 then '是' end isDirectlyStr, #{isDirectly} isDirectly, #{beginTime} beginTime, #{endTime} endTime FROM( select b.lottery_name gameName, b.lottery_type lottType, a.game_code gameCode, count(distinct(a.account)) userCount, IFNUll(sum(a.bet_count), 0) betCount, IFNUll(sum(a.bet_amount), 0) betAmount, IFNUll(sum(a.valid_amount), 0) validAmount, IFNUll(sum(a.win_amount), 0) winAmount from kgnl_bet_daily_detail a left join kgnl_lottery_map b on a.game_code = b.lottery_code
AND a.account = #{account}
and (a.user_paths like concat('%/',#{proxyAccount},'/%') or a.account = #{proxyAccount})
and (a.super_account = #{proxyAccount} or a.account = #{proxyAccount})
AND a.lott_type = #{lottType}
AND a.game_code = #{gameCode}
AND a.stat_time >= #{beginTime}
AND a.stat_time <= #{endTime}
group by a.game_code ) c
ORDER BY ${orderByField} ${orderBySort}

select b.lottery_name gameCodeName, a.account account, a.game_code gameCode, IFNUll(sum(a.bet_amount), 0) betAmount, IFNUll(sum(a.valid_amount), 0) validAmount, IFNUll(sum(a.win_amount), 0) winAmount from kgnl_bet_daily_detail a left join kgnl_lottery_map b on a.game_code = b.lottery_code
AND a.account = #{dto.account}
and (a.user_paths like concat('%/',#{dto.proxyAccount},'/%') or a.account = #{dto.proxyAccount})
and (a.super_account = #{dto.proxyAccount} or a.account = #{dto.proxyAccount})
AND a.lott_type = #{dto.lottType}
AND a.game_code = #{dto.gameCode}
AND a.stat_time >= #{dto.beginTime}
AND a.stat_time <= #{dto.endTime}
group by a.account

select gameCodeName, account, gameCode, betCount, betAmount, validAmount, winAmount, betAmount - Winamount sendPrizeAmount from ( select b.lottery_name gameCodeName, a.account account, a.game_code gameCode, IFNUll(sum(a.bet_count), 0) betCount, IFNUll(sum(a.bet_amount), 0) betAmount, IFNUll(sum(a.valid_amount), 0) validAmount, IFNUll(sum(a.win_amount), 0) winAmount from kgnl_bet_daily_detail a left join kgnl_lottery_map b on a.game_code = b.lottery_code
AND a.account = #{account}
and (a.user_paths like concat('%/',#{proxyAccount},'/%') or a.account = #{proxyAccount})
and (a.super_account = #{proxyAccount} or a.account = #{proxyAccount})
AND a.lott_type = #{lottType}
AND a.game_code = #{gameCode}
AND a.stat_time >= #{beginTime}
AND a.stat_time <= #{endTime}
group by a.account) c

select
IFNUll(sum(a.bet_amount), 0) betAmount,
IFNUll(sum(a.valid_amount), 0) validAmount,
IFNUll(sum(a.win_amount), 0) winAmount
from kgnl_bet_daily_detail a


        AND a.account = #{account}




                and (a.user_paths like concat('%/',#{proxyAccount},'/%') or a.account = #{proxyAccount})


                and (a.super_account = #{proxyAccount} or a.account = #{proxyAccount})




        AND a.lott_type = #{lottType}


        AND a.game_code = #{gameCode}


        AND a.stat_time  >=  #{beginTime}


        AND a.stat_time  <=  #{endTime}
