mapper.select:
SELECT s1.betAmount, s1.validAmount, s1.gameCount, s1.winAmount, s1.userNumber, s1.game_code gameCode, IF(s2.rebateMoney is null,0,s2.rebateMoney) rebateMoney, s1.winAmount-IF(s2.rebateMoney is null,0,s2.rebateMoney) companyAmount from ( SELECT sum(dr.bet_amount) betAmount, sum(dr.valid_amount) validAmount, sum(dr.bet_count) gameCount, sum(dr.win_amount) winAmount, count(DISTINCT dr.account) userNumber, dr.game_code FROM live_user_day_report dr where 1=1
and dr.stat_time >= #{betStartDate}
and dr.stat_time <= #{betEndDate}
and dr.user_paths like #{user_paths}
and dr.account like #{account}
GROUP BY dr.game_code) s1 LEFT JOIN ( SELECT sum(rr.real_rebate_money) rebateMoney, rr.game_code FROM live_rebate_report rr where 1=1
and rr.stat_time >= #{betStartDate}
and rr.stat_time <= #{betEndDate}
and rr.user_paths like #{user_paths}
and rr.account like #{account}
GROUP BY rr.game_code) s2 on s1.game_code=s2.game_code

SELECT t.`member_id`, t.`account`, t.`real_name`, t.`user_paths`, t.`game_code`, t.`stat_time`, t.`game_kind`, t.`first_kind`, SUM(t.`valid_amount`) AS `valid_amount`, u.user_level as userLevel FROM live_member_day_report t left join member u on t.member_id = u.id WHERE valid_amount > 0
AND stat_time = #{startDate}
AND stat_time BETWEEN #{startDate} AND #{endDate}
GROUP BY t.`member_id`, t.`game_code`, t.`game_kind`
