mapper.select:
select account, sum(bank_count) bank_count, sum(bank_money) bank_money, sum(online_count) online_count, sum(online_money) online_money, sum(hand_rech_count) hand_rech_count, sum(hand_rech_money) hand_rech_money, sum(first_rech_money) first_rech_money, sum(rech_discount) rech_discount, sum(other_discount) other_discount, sum(withdraw_count) withdraw_count, sum(withdraw_money) withdraw_money, sum(hand_withdraw_count) hand_withdraw_count, sum(hand_withdraw_money) hand_withdraw_money, sum(first_withdraw_money) first_withdraw_money, sum(counter_fee) counter_fee, sum(bank_money+ online_money +hand_rech_money) totail_recharge_amount, sum(rech_discount + other_discount) totail_discout_amount, sum(withdraw_money+ hand_withdraw_money) totail_withdraw_amount, sum((bank_money+ online_money +hand_rech_money) - (withdraw_money+ hand_withdraw_money)) total_rw_amount from member_rw_report where stat_time BETWEEN #{dto.startTime} and #{dto.endTime}
and account = #{dto.username}
and first_rech_money > 0
group by account having 1 = 1
and sum(bank_count+online_count + hand_rech_count ) >= #{dto.startRNum}
and sum(bank_count+online_count + hand_rech_count ) <= #{dto.endRNum}
and sum(bank_money+online_money+hand_rech_money) >= #{dto.startRAmount}
and sum(bank_money+online_money+hand_rech_money) <= #{dto.endRAmount}
and sum(withdraw_count + hand_withdraw_count) >= #{dto.startWNum}
and sum(withdraw_count + hand_withdraw_count) <= #{dto.endWNum}
and sum(withdraw_money + hand_withdraw_money) >= #{dto.startWAmount}
and sum(withdraw_money + hand_withdraw_money) <= #{dto.endWAmount}

SELECT sum( ifnull( bank_count, 0 )) bankCount, sum( ifnull( bank_money, 0 )) bankMoney, sum( ifnull( online_count, 0 )) onlineCount, sum( ifnull( online_money, 0 )) onlineMoney, sum( ifnull( hand_rech_count, 0 )) handRechCount, sum( ifnull( hand_rech_money, 0 )) handRechMoney, sum( ifnull( first_rech_money, 0 )) firstRechMoney, sum( ifnull( rech_discount, 0 )) rechDiscount, sum( ifnull( other_discount, 0 )) otherDiscount, sum( ifnull( withdraw_count, 0 )) withdrawCount, sum( ifnull( withdraw_money, 0 )) withdrawMoney, sum( ifnull( hand_withdraw_count, 0 )) handWithdrawCount, sum( ifnull( hand_withdraw_money, 0 )) handWithdrawMoney, sum( ifnull( first_withdraw_money, 0 )) firstWithdrawMoney, sum( ifnull( counter_fee, 0 )) counterFee, sum( ifnull( totailRechargeAmount, 0 )) totailRechargeAmount, sum( ifnull( totailDiscoutAmount, 0 )) totailDiscoutAmount, sum( ifnull( totailWithdrawAmount, 0 )) totailWithdrawAmount FROM ( SELECT sum( ifnull( bank_count, 0 )) bank_count, sum( ifnull( bank_money, 0 )) bank_money, sum( ifnull( online_count, 0 )) online_count, sum( ifnull( online_money, 0 )) online_money, sum( ifnull( hand_rech_count, 0 )) hand_rech_count, sum( ifnull( hand_rech_money, 0 )) hand_rech_money, sum( ifnull( first_rech_money, 0 )) first_rech_money, sum( ifnull( rech_discount, 0 )) rech_discount, sum( ifnull( other_discount, 0 )) other_discount, sum( ifnull( withdraw_count, 0 )) withdraw_count, sum( ifnull( withdraw_money, 0 )) withdraw_money, sum( ifnull( hand_withdraw_count, 0 )) hand_withdraw_count, sum( ifnull( hand_withdraw_money, 0 )) hand_withdraw_money, sum( ifnull( first_withdraw_money, 0 )) first_withdraw_money, sum( ifnull( counter_fee, 0 )) counter_fee, sum( ifnull( bank_money, 0 )+ ifnull( online_money, 0 ) + ifnull( hand_rech_money, 0 )) totailRechargeAmount, sum( ifnull( rech_discount, 0 ) + ifnull( other_discount, 0 )) totailDiscoutAmount, sum( ifnull( withdraw_money, 0 )+ ifnull( hand_withdraw_money, 0 )) totailWithdrawAmount FROM member_rw_report where stat_time BETWEEN #{startTime} and #{endTime}
and account = #{username}
and first_rech_money > 0
GROUP BY account ) a where 1 = 1
and (bank_count+online_count + hand_rech_count ) >= #{startRNum}
and (bank_count+online_count + hand_rech_count ) <= #{endRNum}
and (bank_money+online_money+hand_rech_money) >= #{startRAmount}
and (bank_money+online_money+hand_rech_money) <= #{endRAmount}
and (withdraw_count + hand_withdraw_count) >= #{startWNum}
and (withdraw_count + hand_withdraw_count) <= #{endWNum}
and (withdraw_money + hand_withdraw_money) >= #{startWAmount}
and (withdraw_money + hand_withdraw_money) <= #{endWAmount}

SELECT d.count_date, ifnull(a.bank_count,0) bank_count, ifnull(a.bank_money,0) bank_money, ifnull(a.online_count,0) online_count, ifnull(a.online_money,0) online_money, ifnull(a.hand_rech_count,0) hand_rech_count, ifnull(a.hand_rech_money,0) hand_rech_money, ifnull(a.totail_recharge_amount,0) totail_recharge_amount, ifnull(a.rech_discount,0) rech_discount, ifnull(a.other_discount,0) other_discount, ifnull(a.abnormal_income,0) abnormal_income, ifnull(a.withdraw_count,0) withdraw_count, ifnull(a.withdraw_money,0) withdraw_money, ifnull(a.hand_withdraw_count,0) hand_withdraw_count, ifnull(a.hand_withdraw_money,0) hand_withdraw_money, ifnull(a.totail_withdraw_amount,0) totail_withdraw_amount, ifnull(a.counter_fee,0) counter_fee , ifnull(a.abnormal_outcome,0) abnormal_outcome, ifnull(a.total_rw_amount,0) total_rw_amount, ifnull(b.sport_valid_amount,0) sport_valid_amount, ifnull(b.sport_win_amount,0) sport_win_amount, ifnull(b.lottery_valid_amount,0) lottery_valid_amount, ifnull(b.lottery_win_amount,0) lottery_win_amount, ifnull(b.real_valid_amount,0) real_valid_amount, ifnull(b.real_win_amount,0) real_win_amount , ifnull(b.real_water_amount,0) real_water_amount, ifnull(b.totail_game_winAmout,0) totail_game_winAmout FROM tab_qp_date d left JOIN ( SELECT stat_time, sum( bank_count ) bank_count, sum( bank_money ) bank_money, sum( online_count ) online_count, sum( online_money ) online_money, sum( hand_rech_count ) hand_rech_count, sum( hand_rech_money ) hand_rech_money, sum( bank_money + online_money + hand_withdraw_money ) totail_recharge_amount, sum( rech_discount ) rech_discount, sum( other_discount ) other_discount, sum( exception_recharge_amount ) abnormal_income, sum( withdraw_count ) withdraw_count, sum( withdraw_money ) withdraw_money, sum( hand_withdraw_count ) hand_withdraw_count, sum( hand_withdraw_money ) hand_withdraw_money, sum( hand_withdraw_money + withdraw_money ) totail_withdraw_amount, sum( counter_fee ) counter_fee, sum( exception_withdraw_amount ) abnormal_outcome, ( sum( bank_money + online_money + hand_withdraw_money )- sum( hand_withdraw_money + withdraw_money )) total_rw_amount FROM member_rw_report where stat_time BETWEEN #{dto.startTime} and #{dto.endTime}
and super_path like concat('%',#{dto.username},'%')
and account = #{dto.username}
GROUP BY stat_time ) a on d.count_date = a.stat_time left JOIN ( SELECT count_date, sum( sport_valid_amount ) sport_valid_amount, sum( sport_win_amount ) sport_win_amount, sum( lottery_valid_amount ) lottery_valid_amount, sum( lottery_win_amount ) lottery_win_amount, sum( real_valid_amount ) real_valid_amount, sum( real_win_amount ) real_win_amount, sum( real_water_amount ) real_water_amount, sum( lottery_win_amount + sport_win_amount + real_win_amount ) totail_game_winAmout FROM member_day_report where count_date BETWEEN #{dto.startTime} and #{dto.endTime}
and agent_path like concat('%',#{dto.username},'%')
and user_name = #{dto.username}
GROUP BY count_date ) b ON d.count_date = b.count_date where d.count_date BETWEEN #{dto.startTime} and #{dto.endTime}

SELECT ifnull( a.bank_count, 0 ) bank_count, ifnull( a.bank_money, 0 ) bank_money, ifnull( a.online_count, 0 ) online_count, ifnull( a.online_money, 0 ) online_money, ifnull( a.hand_rech_count, 0 ) hand_rech_count, ifnull( a.hand_rech_money, 0 ) hand_rech_money, ifnull( a.totail_recharge_amount, 0 ) totail_recharge_amount, ifnull( a.rech_discount, 0 ) rech_discount, ifnull( a.other_discount, 0 ) other_discount, ifnull( a.abnormal_income, 0 ) abnormal_income, ifnull( a.withdraw_count, 0 ) withdraw_count, ifnull( a.withdraw_money, 0 ) withdraw_money, ifnull( a.hand_withdraw_count, 0 ) hand_withdraw_count, ifnull( a.hand_withdraw_money, 0 ) hand_withdraw_money, ifnull( a.totail_withdraw_amount, 0 ) totail_withdraw_amount, ifnull( a.counter_fee, 0 ) counter_fee, ifnull( a.abnormal_outcome, 0 ) abnormal_outcome, ifnull( a.total_rw_amount, 0 ) total_rw_amount, ifnull( b.sport_valid_amount, 0 ) sport_valid_amount, ifnull( b.sport_win_amount, 0 ) sport_win_amount, ifnull( b.lottery_valid_amount, 0 ) lottery_valid_amount, ifnull( b.lottery_win_amount, 0 ) lottery_win_amount, ifnull( b.real_valid_amount, 0 ) real_valid_amount, ifnull( b.real_win_amount, 0 ) real_win_amount, ifnull( b.real_water_amount, 0 ) real_water_amount, ifnull( b.totail_game_winAmout, 0 ) totail_game_winAmout FROM ( SELECT 'aa' type, sum( bank_count ) bank_count, sum( bank_money ) bank_money, sum( online_count ) online_count, sum( online_money ) online_money, sum( hand_rech_count ) hand_rech_count, sum( hand_rech_money ) hand_rech_money, sum( bank_money + online_money + hand_withdraw_money ) totail_recharge_amount, sum( rech_discount ) rech_discount, sum( other_discount ) other_discount, sum( exception_recharge_amount ) abnormal_income, sum( withdraw_count ) withdraw_count, sum( withdraw_money ) withdraw_money, sum( hand_withdraw_count ) hand_withdraw_count, sum( hand_withdraw_money ) hand_withdraw_money, sum( hand_withdraw_money + withdraw_money ) totail_withdraw_amount, sum( counter_fee ) counter_fee, sum( exception_withdraw_amount ) abnormal_outcome, ( sum( bank_money + online_money + hand_withdraw_money )- sum( hand_withdraw_money + withdraw_money )) total_rw_amount FROM member_rw_report WHERE stat_time BETWEEN #{startTime} and #{endTime}
and super_path like concat('%',#{username},'%')
and account = #{username}
) a LEFT JOIN ( SELECT 'aa' type, sum( sport_valid_amount ) sport_valid_amount, sum( sport_win_amount ) sport_win_amount, sum( lottery_valid_amount ) lottery_valid_amount, sum( lottery_win_amount ) lottery_win_amount, sum( real_valid_amount ) real_valid_amount, sum( real_win_amount ) real_win_amount, sum( real_water_amount ) real_water_amount, sum( lottery_win_amount + sport_win_amount + real_win_amount ) totail_game_winAmout FROM member_day_report WHERE count_date BETWEEN #{startTime} and #{endTime}
and agent_path like concat('%',#{username},'%')
and user_name = #{username}
) b ON a.type = b.type

SELECT t.userName FROM ( SELECT user_name userName, sum( valid_amount ) validAmount FROM member_day_report
AND count_date >= #{startTime}
AND count_date <= #{endTime}
GROUP BY user_id, user_name ) t WHERE validAmount >= #{minBetAmount}
