mapper.update:
UPDATE member_info fo,member m SET fo.balance = 0 WHERE fo.member_id = m.id and m.user_type = 'P'
and m.account in
#{item}

mapper.select:
SELECT a.user_id memberId, a.user_name account, a.change_growth changes, a.type, b.vip_level vip, b.vip_growth growth FROM member_growth_record a LEFT JOIN member_info b on b.member_id = a.user_id WHERE 1=1
and a.type = #{type}
and a.user_id = #{memberId}
and a.remark = #{remark}
