mapper.select:
select m.id memberId, m.account, m.user_level userLevel, m.super_path superPath, i.vip_level vipLevel, i.balance memberBalance, g.loan_money loanMoney, l.loan_time loanTime, IFNULL(l.loan_status, 1) loanStatus, IFNULL(l.overdraft_money, 0.0000) overdraftMoney from (select id,account,user_level,super_path from member
status = 1
AND account = #{dto.account}
) as m left join member_info i on m.id = i.member_id left join member_growth_level g on g.level = i.vip_level left join ( SELECT id, loan_time, loan_status, overdraft_money, member_id FROM member_loan a WHERE NOT EXISTS (select 1 from member_loan b where a.member_id = b.member_id and a.id < b.id)) l ON i.member_id = l.member_id
AND i.vip_level = #{dto.vipLevel}
AND IFNULL(l.overdraft_money, 0.0000) BETWEEN #{dto.minOverdraftMoney} and #{dto.maxOverdraftMoney}
AND l.loan_time BETWEEN #{dto.beginLoanTime} and #{dto.endLoanTime}
AND IFNULL(l.loan_status, 1) = #{dto.loanStatus}
