mapper.select:
select user_name userName, ifnull(sum(reword_amount),0) rewordAmount, `type` from member_weal_reword where draw_time between #{startTime} and #{endTime}
and user_name = #{userName}
and status = #{status}
GROUP BY `type`

select ifnull(sum(reword_amount),0) totalUpRewardAmount from member_weal_reword where draw_time between #{startTime} and #{endTime} and status = 2 and `type` = 0
and user_name = #{userName}
AND parent_name = #{superAccount}
AND agent_path like concat('%',#{superAccount},'%')
UNION ALL select ifnull(sum(reword_amount),0) totalWeekWageAmount from member_weal_reword where draw_time between #{startTime} and #{endTime} and status = 2 and `type` = 1
and user_name = #{userName}
AND parent_name = #{superAccount}
AND agent_path like concat('%',#{superAccount},'%')
UNION ALL select ifnull(sum(reword_amount),0) totalMonthWageAmount from member_weal_reword where draw_time between #{startTime} and #{endTime} and status = 2 and `type` = 2
and user_name = #{userName}
AND parent_name = #{superAccount}
AND agent_path like concat('%',#{superAccount},'%')
UNION ALL select ifnull(sum(reword_amount),0) totalBirthGiftMoneyAmount from member_weal_reword where draw_time between #{startTime} and #{endTime} and status = 2 and `type` = 3
and user_name = #{userName}
AND parent_name = #{superAccount}
AND agent_path like concat('%',#{superAccount},'%')
UNION ALL select ifnull(sum(reword_amount),0) totalMonthRedEnvelopeAmount from member_weal_reword where draw_time between #{startTime} and #{endTime} and status = 2 and `type` = 4
and user_name = #{userName}
AND parent_name = #{superAccount}
AND agent_path like concat('%',#{superAccount},'%')
