mapper.select:
        select IFNULL(max(maxRatio),0) as minRatio from (
            select JSON_UNQUOTE(
                    json_extract(
                            json_extract(
                                sl.divide_config,CONCAT('$.',concat('"', concat(#{code},'"')))),CONCAT('$.',concat('"', concat('divideRatio','"'))
                            )
                        )
                    ) AS maxRatio
            from spread_link_info sl
	        where sl.agent_account = #{userName} and sl.divide_config != '' and sl.user_type = 1
            and sl.divide_config is not null
	    ) tmp
