mapper.select:



        AND menu_name LIKE concat('%', #{menuName}, '%')


        AND visible = #{visible}


ORDER BY parent_id, menu_sort

mapper.update:
UPDATE sys_menu
menu_name = #{menuName}, title = #{title}, parent_name = #{parentName}, parent_id = #{parentId}, parent_name = #{parentName}, menu_sort = #{menuSort}, component = #{component}, path = #{path}, url = #{url}, menu_type = #{menuType}, visible = #{visible}, i_frame = #{iFrame}, cache_flag = #{cacheFlag}, perms = #{perms}, icon = #{icon}, status = #{status}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
WHERE menu_id = #{menuId}
